    override fun onNavigationReady(isRunning: Boolean) {
        if (!isRunning && !::navigationMapboxMap.isInitialized) {
            if (navigationView.retrieveNavigationMapboxMap() != null) {
                this.navigationMapboxMap = navigationView.retrieveNavigationMapboxMap()!!
                navigationView.retrieveMapboxNavigation()?.let {
                    this.mapboxNavigation = it
                }
                val optionsBuilder = NavigationViewOptions.builder(this)
                optionsBuilder.navigationListener(this)
//                optionsBuilder.directionsRoute(route)
                optionsBuilder.directionsRoute(getDirectionsRoute2())
                optionsBuilder.shouldSimulateRoute(this.enableSimulate)
                optionsBuilder.navigationFeedbackOptions(
                    NavigationFeedbackOptions.Builder().enableArrivalExperienceFeedback(false)
                        .enableDetailedFeedbackAfterNavigation(false).build()
                )

                navigationView.startNavigation(optionsBuilder.build())
            }
        }
    }

